INSERT INTO `commentary` (`date`, `content`, `Interaction_idInteraction`) VALUES
-- Película 1 (IDs: 1, 10, 18, 23)
('2025-04-01', 'Una de mis favoritas, todo encaja perfectamente.', 1),
('2025-04-02', 'Totalmente de acuerdo, tiene una narrativa brillante.', 10),
('2025-04-03', 'No me convenció tanto, esperaba más del final.', 18),
('2025-04-04', 'A mí sí me encantó, sobre todo el giro final.', 23),

-- Película 2 (IDs: 2, 11, 24)
('2025-04-05', 'Visualmente impecable pero floja en el guion.', 2),
('2025-04-06', 'Discrepo, creo que el guion es su punto fuerte.', 11),
('2025-04-07', 'Ambas cosas me parecieron excelentes.', 24),

-- Película 3 (IDs: 3, 12, 19, 25)
('2025-04-08', 'La actuación principal se roba la pantalla.', 3),
('2025-04-09', 'Concuerdo, pero el villano fue decepcionante.', 12),
('2025-04-10', 'El villano es lo mejor de la película, en mi opinión.', 19),
('2025-04-11', 'Yo estoy con vos, me pareció un gran villano.', 25),

-- Película 4 (IDs: 13, 20, 26)
('2025-04-12', 'El ritmo es muy irregular, pero se deja ver.', 13),
('2025-04-13', 'Sí, aunque el tercer acto me enganchó mucho.', 20),
('2025-04-14', 'A mí me pareció demasiado lenta en general.', 26),

-- Película 5 (IDs: 4, 14, 21, 27)
('2025-04-15', 'No es mala, pero esperaba más de esta producción.', 4),
('2025-04-16', 'A mí me sorprendió gratamente.', 14),
('2025-04-17', '¿En serio? Me aburrió.', 21),
('2025-04-18', 'Yo también creo que fue entretenida.', 27),

-- Película 6 (IDs: 5, 15, 28)
('2025-04-19', 'Demasiado predecible.', 5),
('2025-04-20', 'Sí, pero tiene escenas memorables.', 15),
('2025-04-21', 'El final fue lo único destacable.', 28),

-- Película 7 (IDs: 6, 16, 29)
('2025-04-22', '¡La amé! Qué experiencia tan intensa.', 6),
('2025-04-23', 'Para mí fue solo ruido sin sustancia.', 16),
('2025-04-24', 'Creo que hay que verla más de una vez.', 29),

-- Película 8 (IDs: 7, 22, 30)
('2025-04-25', 'Me gustó la ambientación, muy inmersiva.', 7),
('2025-04-26', '¡Totalmente! La atmósfera es espectacular.', 22),
('2025-04-27', 'Coincido, aunque le faltó algo de acción.', 30),

-- Película 9 (IDs: 8, 17, 32)
('2025-04-28', 'Una decepción total.', 8),
('2025-04-29', 'No creo que sea tan mala.', 17),
('2025-04-30', 'Es cierto que no es para todos los gustos.', 31),

-- Película 10 (IDs: 9, 33, 32)
('2025-05-01', 'Qué joya, no entiendo cómo pasó tan desapercibida.', 9),
('2025-05-02', '¡Tal cual! Yo la recomendaría sin pensarlo.', 33),
('2025-05-03', 'Tiene sus fallas, pero deja buen sabor.', 32),

-- Comentarios adicionales (reacciones cruzadas o fuera de hilo)
('2025-05-04', 'La música de la Orgullo y Prejuicio aún me pone la piel de gallina.', 24),
('2025-05-05', 'La escena del tren en Mamma Mia! fue icónica.', 29),
('2025-05-06', 'Me reí más de lo que esperaba en la El Corredor del Laberinto.', 31),
('2025-05-07', 'Los Juegos del Hambre tiene uno de los mejores plot twists del año.', 26);

